// dp[i][j]表示s下表i,j能否构成有效的括号组合，我们知道如果i-j是奇数个个数，那么它一定不是有效括号
// 如果i,j相邻，只有'()'才是一个有效的括号
// i-j是偶数个个数的时候，它如果是一个有效括号，他们肯定存在一种组合使得i-p,j-p都是有效括号组合，或者i+1,j-1是有效组合且s[i]=(,s[j]=)
// 最终的最长的有效括号组合即是dp中的最大数
// 超时
func longestValidParentheses(s string) int {
	if len(s) <= 1 {
		return 0
	}
	ret := 0
	dp := make([][]int, len(s))
	for i := range dp {
		dp[i] = make([]int, len(s))
	}
	for gap := 1; gap < len(dp); gap++ {
		for i := 0; i+gap < len(dp); i++ {
			if gap == 1 {
				if s[i] != '(' || s[i+gap] != ')' { // base
					continue
				}
				dp[i][i+gap] = 2
				if dp[i][i+gap] > ret {
					ret = dp[i][i+gap]
				}
			} else if gap%2 == 0 {
				dp[i][i+gap] = 0
			} else {
				if s[i] != '(' || s[i+gap] != ')' {
					continue
				}
				if dp[i+1][i+gap-1] > 0 {
					dp[i][i+gap] = dp[i+1][i+gap-1] + 2
					if dp[i][i+gap] > ret {
						ret = dp[i][i+gap]
					}
				} else {
					for p := i + 1; p < i+gap-1; p += 2 {
						if dp[i][p] > 0 && dp[p+1][i+gap] > 0 {
							dp[i][i+gap] = dp[i][p] + dp[p+1][i+gap]
							if dp[i][i+gap] > ret {
								ret = dp[i][i+gap]
							}
							break
						}
					}
				}
			}
		}
	}
	return ret
}

// 栈实现
func longestValidParentheses1(s string) int {
	max := 0
	stack := make([]int, 0)
	stack = append(stack, -1)
	for i := 0; i < len(s); i++ {
		if s[i] == '(' {
			stack = append(stack, i)
		} else {
			stack = stack[:len(stack)-1]
			if len(stack) == 0 {
				stack = append(stack, i)
			} else {
				max = common.Max(max, i-stack[len(stack)-1])
			}
		}
	}
	return max
}
